$(function () {

    // Subdivision Module Reorder
    function subdivisionModuleReorder() {
        if ($('#community-nav .nav-tabs a[href="#sub-listings"]').length > 0) {
            $('#community-nav .nav-tabs a[href="#sub-listings"]').parent().prependTo('#community-nav .nav-tabs');
            if (window.location.hash) {
            } else {
                $('#community-nav .nav-tabs a[href="#sub-listings"]').click();
            }
        }
    }
    subdivisionModuleReorder();

    // Search Suggest
    function quickSearchSuggest() {
        $('#qs_suggest').each(function (i) {
            var qs_suggestId = "qs_suggest_" + i;
            this.id = qs_suggestId;
            $('.mls-results').hide();
            $(this).magicSuggest({
                hideTrigger: true,
                minChars: 2,
                maxSuggestions: 100,
                allowFreeEntries: true,
                placeholder: 'Search by MLS # or Address',
                maxSelection: 15,
                inputCfg: { 'autocomplete': 'true' },
                name: 'data[Listing][mlsnumbers]',
                data: '/listings/ajax_mlsdata',
                displayField: 'name',
                renderer: function (a) { if (typeof (a.display) == 'undefined' || a.display == '') { return a.id; /*var mlsids = a.id; console.log(mlsids); return mlsids.split(",");*/ } else { return a.display; } },
                selectionRenderer: function (a) { if (typeof (a.display) == 'undefined' || a.display == '') { return a.id; /*var mlsids = a.id; console.log(mlsids); return mlsids.split(",");*/ } else { return a.display; } },
            });
            $(this).on('beforeload', function () {
                $(".mls-results").show().spin({ lines: 9, length: 1, width: 4, radius: 4, speed: 1.3, left: '0px' });
            });

            $(this).on('load', function () {
                $(".mls-results").hide().spin(false);
            });
        });
    }
    quickSearchSuggest();

    // Lazyload
    $('.lazy').Lazy({
        scrollDirection: 'vertical',
        threshold: 0
    });

    // Use listing image as background image
    function propertyDetailsHeader() {
        if ($('.listings_view .royalSlider').length) {
            var imgURL = $('.listings_view .royalSlider .rsNavSelected img').attr('src');
            $('.banner.section__background').css('background-image', 'url(' + imgURL + ')');
        }
    }
    propertyDetailsHeader();

    // Sidebar Scroll List Box
    function sidebarScrollList() {
        $(".sidebar__list-scroll").each(function (i) {
            $(this).attr('id', 'list' + i);
        });
        $(".sidebar__list-scroll .list-slider ul").each(function (i, item) {
            var slidesShowScroll = $(this).parent().data('list-items');
            slidesShowScroll = (slidesShowScroll <= 0) ? slidesShowScroll = 1 : slidesShowScroll;
            if (slidesShowScroll == null) {
                slidesShowScroll = 6;
            }
            var carouselId = "carousel" + i;
            this.id = carouselId;
            $(this).slick({
                vertical: true,
                verticalSwiping: true,
                slidesToShow: slidesShowScroll,
                slidesToScroll: slidesShowScroll,
                prevArrow: '<button type="button" class="slick-prev btn btn__light__outline"><i class="fa fa-angle-up"></i></button>',
                nextArrow: '<button type="button" class="slick-next btn btn__light__outline"><i class="fa fa-angle-down"></i></button>',
                appendArrows: $('<div></div>').addClass('section__slider-arrows').appendTo('.sidebar__list-scroll#list' + i + ' h4'),
            });
        });
    }
    sidebarScrollList();

    // Sidebar Multilevel List Box
    function multilevelList() {
        $('.sidebar__multilevel-list').each(function (i) {
            $(this).attr('id', 'multilevel_' + i);
            $('ul:first', this).smartmenus('destroy').removeAttr('id class style').addClass('sm sm-vertical-list').smartmenus();
        });
    }
    multilevelList();

    // Homepage Property Slider
    function propertySlider() {
        $('.property-slider:not(.sidebar .property-slider)').each(function (i) {
            $(this).attr('id', 'slide_' + i);
        });
        $('.property-slider:not(.sidebar .property-slider) .slider-wrap').each(function (i, item) {
            var carouselId = "carousel_" + i;
            this.id = carouselId;
            $(this).slick({
                slidesToShow: 4,
                slidesToScroll: 4,
                rows: 2,
                prevArrow: '<button type="button" class="slick-prev btn btn__primary__outline"><i class="fa fa-angle-left"></i> Prev</button>',
                nextArrow: '<button type="button" class="slick-next btn btn__primary__outline">Next <i class="fa fa-angle-right"></i></button>',
                appendArrows: $('<div></div>').addClass('section__slider-arrows').appendTo($(this).parent()),
                responsive: [
                    {
                        breakpoint: 1680,
                        settings: {
                            slidesToShow: 3,
                            slidesToScroll: 3,
                        }
                    },
                    {
                        breakpoint: 992,
                        settings: {
                            slidesToShow: 2,
                            slidesToScroll: 2
                        }
                    },
                    {
                        breakpoint: 767,
                        settings: {
                            slidesToShow: 1,
                            slidesToScroll: 1
                        }
                    }
                ]
            });
        });

    }
    propertySlider();

    // Sidebar Property Slider
    function propertySliderSidebar() {
        $('.sidebar .property-slider').each(function (i) {
            $(this).attr('id', 'sidebar_slide_' + i);
            $('.sidebar .property-slider#sidebar_slide_' + i + ' .slider-wrap').html($('.sidebar .property-slider#sidebar_slide_' + i + ' .item').sort(function () {
                return Math.random() - 0.5;
            }));
        });
        $('.sidebar .property-slider .slider-wrap').each(function (i, item) {
            var carouselId = "carousel_" + i;
            this.id = carouselId;
            $(this).slick({
                adaptiveHeight: true,
                prevArrow: '<button type="button" class="slick-prev btn btn__primary__outline"><i class="fa fa-angle-left"></i> Prev</button>',
                nextArrow: '<button type="button" class="slick-next btn btn__primary__outline">Next <i class="fa fa-angle-right"></i></button>',
                appendArrows: $('<div></div>').addClass('section__slider-arrows').appendTo($(this).parent()),
            });
        });
    }
    propertySliderSidebar();

    // Homepage Agent Slider
    function agentSlider() {
        $('.agent-slider:not(.sidebar .agent-slider)').each(function (i) {
            $(this).attr('id', 'slide_' + i);
            $('.agent-slider:not(.sidebar .agent-slider)#slide_' + i + ' .slider-wrap').html($('.agent-slider:not(.sidebar .agent-slider)#slide_' + i + ' .slider-wrap .agent').sort(function () {
                return Math.random() - 0.5;
            }));
        });
        $('.agent-slider:not(.sidebar .agent-slider) .slider-wrap').each(function (i, item) {
            var carouselId = "carousel_" + i;
            this.id = carouselId;
            $(this).slick({
                slidesToShow: 5,
                slidesToScroll: 5,
                prevArrow: '<button type="button" class="slick-prev btn btn__primary__outline"><i class="fa fa-angle-left"></i> Prev</button>',
                nextArrow: '<button type="button" class="slick-next btn btn__primary__outline">Next <i class="fa fa-angle-right"></i></button>',
                appendArrows: $('<div></div>').addClass('section__slider-arrows').appendTo($(this).parent()),
                responsive: [
                    {
                        breakpoint: 1680,
                        settings: {
                            slidesToShow: 4,
                            slidesToScroll: 4,
                        }
                    },
                    {
                        breakpoint: 1440,
                        settings: {
                            slidesToShow: 3,
                            slidesToScroll: 3,
                        }
                    },
                    {
                        breakpoint: 1100,
                        settings: {
                            slidesToShow: 3,
                            slidesToScroll: 3,
                        }
                    },
                    {
                        breakpoint: 992,
                        settings: {
                            slidesToShow: 2,
                            slidesToScroll: 2
                        }
                    },
                    {
                        breakpoint: 767,
                        settings: {
                            slidesToShow: 1,
                            slidesToScroll: 1
                        }
                    }
                ]
            })
        });
    }
    agentSlider();

    // Sidebar Agent Slider
    function agentSliderSidebar() {
        $('.sidebar .agent-slider').each(function (i) {
            $(this).attr('id', 'sidebar_slide_' + i);
            $('.sidebar .agent-slider#sidebar_slide_' + i + ' .slider-wrap').html($('.sidebar .agent-slider#sidebar_slide_' + i + ' .slider-wrap .agent').sort(function () {
                return Math.random() - 0.5;
            }));
        });
        $('.sidebar .agent-slider .slider-wrap').each(function (i, item) {
            var carouselId = "carousel_" + i;
            this.id = carouselId;
            $(this).slick({
                adaptiveHeight: true,
                prevArrow: '<button type="button" class="slick-prev btn btn__primary__outline"><i class="fa fa-angle-left"></i> Prev</button>',
                nextArrow: '<button type="button" class="slick-next btn btn__primary__outline">Next <i class="fa fa-angle-right"></i></button>',
                appendArrows: $('<div></div>').addClass('section__slider-arrows').appendTo($(this).parent()),
            });
        });
    }
    agentSliderSidebar();

    // RoyalSlider Fullscreen Hide Header
    function royalSliderFullscreen() {
        if ($('.listings_view .royalSlider').length) {
            var slider = $(".royalSlider").data('royalSlider');
            slider.ev.on('rsEnterFullscreen', function () {
                $('.header').hide();
            });
            slider.ev.on('rsExitFullscreen', function () {
                $('.header').show();
            });
        };
    }
    royalSliderFullscreen();

    // svg4everybody
    svg4everybody();

    // SmartMenus
    $('.header #main_navigation').smartmenus();
    $('.mobile-menu #main_navigation').smartmenus();

    // Mobile Navigation
    $('.header__mobile-menu-btn').click(function (e) {
        e.preventDefault();
        $('.mobile-menu').toggleClass('mobile-menu__open');
        $('body').toggleClass('mobile-menu-open');
        $(this).toggleClass('open');
    });

    // Quicksearch Single Select
    $('.qs-single-select').selectize({
        create: false,
        hideSelected: true,
        allowEmptyOption: true,
        placeholder: $(this).data('placeholder'),
    });

    // Quicksearch Multiple Select
    $('#qs_form .multi-select.placeholder').remove();
    $('#qs_form .multi-select').show();
    $('#qs_form .multi-select').multipleSelect({
        minimumCountSelected: 2,
        placeholder: $(this).data('placeholder'),
    });

    // Navbar Scroll
    function headerHeadroom() {
        var header = document.querySelector(".header .header__meta");

        if (window.location.hash) {
            header.classList.add("headroom--unpinned");
        }
        var headroom = new Headroom(header, {
            tolerance: {
                down: 10,
                up: 20
            },
            offset: $('.admin-navbar').outerHeight() + $('.header').outerHeight(),
        });
        headroom.init();
    }
    headerHeadroom();

    // Header padding
    function headerCalculateContentPadding() {
        $('body.int').css('padding-top', $('.header').height() + 'px');
    }
    $(window).resize(function () {
        headerCalculateContentPadding();
    });
    headerCalculateContentPadding();

    // Responsive Embed
    function responsiveVideoEmbed() {
        var videoSelectors = [
            //'iframe[src*="player.vimeo.com"]',
            'iframe[src*="youtube.com"]',
            'iframe[src*="youtube-nocookie.com"]',
            'iframe[src*="kickstarter.com"][src*="video.html"]',
            'iframe[src*="screenr.com"]',
            'iframe[src*="blip.tv"]',
            'iframe[src*="dailymotion.com"]',
            'iframe[src*="viddler.com"]',
            'iframe[src*="qik.com"]',
            'iframe[src*="revision3.com"]',
            'iframe[src*="hulu.com"]',
            'iframe[src*="funnyordie.com"]',
            'iframe[src*="flickr.com"]',
            'embed[src*="v.wordpress.com"]'
        ];
        var allVideos = videoSelectors.join(',');
        $(allVideos).wrap('<span class="fluid-video" />');
    };
    responsiveVideoEmbed();

    // Enable tooltips
    $('[data-toggle="tooltip"]').tooltip({
        delay: {
            "show": 500,
            "hide": 500
        }
    });

    // Homepage Agent Testimonials
    function agentTestimonials() {
        $('.testimonials-slider .slider-wrap').html($('.testimonials-slider blockquote').sort(function () {
            return Math.random() - 0.5;
        }));
        $('.testimonials-slider .slider-wrap').slick({
            adaptiveHeight: true,
            prevArrow: '<button type="button" class="slick-prev btn btn__primary__outline"><i class="fa fa-angle-left"></i> Prev</button>',
            nextArrow: '<button type="button" class="slick-next btn btn__primary__outline">Next <i class="fa fa-angle-right"></i></button>',
            appendArrows: $('<div></div>').addClass('section__slider-arrows').appendTo('.testimonials-slider'),
        });
    }
    agentTestimonials();

    // Hero Slider
    function heroImage() {
        $('.hero__media__slider .slider').slick({
            fade: true,
            autoplay: true,
            autoplaySpeed: 10000,
            prevArrow: '<button type="button" class="slick-prev"><svg role="img" title="Left Arrow"><use xlink:href="img/tm-07/icon-pack.svg#left-arrow"></use></svg></button>',
            nextArrow: '<button type="button" class="slick-next"><svg role="img" title="Right Arrow"><use xlink:href="img/tm-07/icon-pack.svg#right-arrow"></use></svg></button>',
            appendArrows: $('<div></div>').addClass('section__slider-arrows hidden-xs').appendTo('.hero__search__links'),
        });
    }

    // Hero Video
    function heroVideo() {
        var videoContainer = '.hero__video';
        $(videoContainer).YTPlayer({
            videoURL: $(videoContainer).data('video-url'),
            containment: videoContainer,
            loop: true,
            autoPlay: true,
            showControls: false,
            abundance: 0,
            stopMovieOnBlur: false,
            playOnlyIfVisible: true,
            onScreenPercentage: 100
        });
    }

    // Check if media type is defind and default if empty
    function heroMediaCheck() {
        var checkMediaType = $.trim($('.hero__media').data('media-type'));
        if (checkMediaType == 'video') {
            $('.hero__media__slider').remove();
            $('.hero__media__video').show();
            heroVideo();
        } else if (checkMediaType == 'image') {
            $('.hero__media__video').remove();
            $('.hero__media__slider').show();
            heroImage();
        } else {
            $('.hero__media__video').remove();
            $('.hero__media__slider').show();
            heroImage();
        }
    }
    heroMediaCheck();

});